import React from "react";
import { useState } from "react/cjs/react.development";
import FormInput from "./FormInput";
import Table from "./Table";
import axios from 'axios'

const URI = 'http://localhost:8080/spiral'

const CompCreateSpiral = () => {

    const [rows, setRows] = useState('3')
    const [columns, setColumns] = useState('3')
    const [spiral, setSpiral] = useState([])

    const changeRows = (event) => {
        setRows(event.target.value);
    }

    const changeColumms = (event) => {
        setColumns(event.target.value);
    }

    const getSpiral = async () => {   
        const res = await axios.get(URI+'?cols=' + rows + '&rows=' + columns) 
        console.log(res)    
        setSpiral(res.data.rows)
    }

    return(
        <div className='container'>
            <form className="form-inline row" onSubmit={getSpiral}>
                <div className="form-group col">
                    <label>Number of Rows</label>
                    <input value={rows} onChange={changeRows} type="text" className="form-control"/>
                </div>
                <div className="form-group col">
                    <label>Number of Columns</label>
                    <input value={columns} onChange={changeColumms}  type="text" className="form-control"/>
                </div>
                <button type="submit" className="btn btn-primary btn-sm col">Calculate</button>
            </form>
            <div className='row'>
                <div className='col'>
                    <table className='table table-bordered'>
                        <tbody>
                            {spiral.map ((currentRow) =>(
                                <tr key={currentRow.toString()}>
                                    {currentRow.map ((currentColumn) =>(
                                        <td key={Math.random()}>{currentColumn}</td>
                                    ))}
                                </tr>
                            ))}         
                        </tbody>
                    </table>
                </div>
            </div>

        </div>
    )

}

export default CompCreateSpiral
