import axios from 'axios'
import {useState, useEffect} from 'react'
import {Link} from 'react-router-dom'

const URI = 'http://localhost:8080/spiral?cols=3&rows=3'

const CompCreateSpiral = () => {
    const[spiral, setSpiral] = useState([])
    useEffect( () => {
        getSpiral()
    }, [])

    const getSpiral = async () => {
        console.log(URI)    
        const res = await axios.get(URI) 
        console.log(res)    
        setSpiral(res.data.rows)
    }


    return(
        <div className='container'>
            <div className='row'>
                <div className='col'>
                    <table className='table-primary'>
                        <tbody>
                            {spiral.map ((currentRow) =>(
                                <tr key={currentRow.toString()}>
                                    {currentRow.map ((currentColumn) =>(
                                        <td>{currentColumn}</td>
                                    ))}
                                </tr>
                            ))}

                        </tbody>
                    </table>
                </div>
            </div>

        </div>
    )

}

export default CompCreateSpiral
